#
# Table structure for table 'ai_users'
#

DROP TABLE IF EXISTS ai_users;
CREATE TABLE `ai_users` (
  `uid` int(10) unsigned NOT NULL auto_increment,
  `gid` smallint(5) unsigned NOT NULL default '0',
  `_status` enum('Y','N') NOT NULL default 'Y',
  `_pass` varchar(32) NOT NULL default '',
  `_login` varchar(50) NOT NULL default '',
  `_name` varchar(255) NOT NULL default '',
  `_mail` varchar(100) NOT NULL default '',
  `_date_register` int(10) unsigned NOT NULL default '0',
  `_date_last` int(10) unsigned NOT NULL default '0',
  `_ip_register` varchar(15) NOT NULL default '',
  `_ip_last` varchar(15) NOT NULL default '',
  `_pages` varchar(255) NOT NULL default '',
  PRIMARY KEY  (`uid`)
) TYPE=MyISAM;



